// Codewars 20
// Title :  Interleaving Arrays
// Instructions :   Create a function, that accepts an arbitrary number of arrays and returns a single array generated by alternately appending elements from the passed in arguments.
// Challange Url :  https://www.codewars.com/kata/523d2e964680d1f749000135/train/javascript
// Level : 5kyu

// Method : 1
export function interleave(...arrays) {
  const maxLength = Math.max(...arrays.map((a) => a.length));
  const result = [];
  for (let i = 0; i < maxLength; i++) {
    for (const arr of arrays) {
      result.push(i < arr.length ? arr[i] : null);
    }
  }
  return result;
}

// Method 2

// Other Users Method
const interleave = (...a) => {
  let interleaved = [];
  const longest = a.reduce((a, b) => (a.length > b.length ? a : b));

  for (let i in longest) {
    a.forEach((word) =>
      word[i] === undefined ? interleaved.push(null) : interleaved.push(word[i])
    );
  }
  return interleaved;
};
